(48, 'woocommerce-shipping-fedex-api-outage-2023-07-16', 'warning', 'en_US', 'Scheduled FedEx API outage — July 2023', 'On July 16 there will be a full outage of the FedEx API from 04:00 to 08:00 AM UTC. Due to planned maintenance by FedEx, you\'ll be unable to provide FedEx shipping rates during this time. Follow the link below for more information and recommendations on how to minimize impact.', '{}', 'pending', 'woocommerce.com', '2024-04-22 13:15:23', NULL, 0, 'plain', '', 0, 0, 'info'),
(49, 'wcship-2023-07-hazmat-update-needed', 'update', 'en_US', 'Action required: USPS HAZMAT compliance update for WooCommerce Shipping & Tax extension', '<strong>Your store requires an update for the WooCommerce Shipping extension</strong>. Please update to the latest version of the WooCommerce Shipping &amp; Tax extension immediately to ensure compliance with new USPS HAZMAT rules currently in effect.', '{}', 'pending', 'woocommerce.com', '2024-04-22 13:15:23', NULL, 0, 'plain', '', 0, 0, 'info'),
(50, 'woocommerce-WCStripe-Aug-2023-update-needed', 'update', 'en_US', 'Action required: Security update for WooCommerce Stripe plugin', '<strong>Your shop requires an important security update for the  WooCommerce Stripe plugin</strong>. The fix for this vulnerability was released on July 31. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2024-04-22 13:15:23', NULL, 0, 'plain', '', 0, 0, 'info'),
(51, 'woocommerce-WCStripe-Aug-2023-security-updated', 'update', 'en_US', 'Security update of WooCommerce Stripe plugin', '<strong>Your store has been updated to the latest secure version of the WooCommerce Stripe plugin</strong>. This update was released on July 31.', '{}', 'pending', 'woocommerce.com', '2024-04-22 13:15:23', NULL, 0, 'plain', '', 0, 0, 'info'),
(52, 'woocommerce-WooPayments-Aug-2023-update-needed', 'update', 'en_US', 'Action required: Security update for WooPayments (WooCommerce Payments) plugin', '<strong>Your shop requires an important security update for the WooPayments (WooCommerce Payments) extension</strong>. The fix for this vulnerability was released on July 31. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2024-04-22 13:15:23', NULL, 0, 'plain', '', 0, 0, 'info'),
(53, 'woocommerce-WooPayments-Aug-2023-security-updated', 'update', 'en_US', 'Security update of WooPayments (WooCommerce Payments) plugin', '<strong>Your store has been updated to the more secure version of WooPayments (WooCommerce Payments)</strong>. This update was released on July 31.', '{}', 'pending', 'woocommerce.com', '2024-04-22 13:15:23', NULL, 0, 'plain', '', 0, 0, 'info'),
(54, 'avalara_q3-2023_noAvaTax', 'marketing', 'en_US', 'Automatically calculate VAT in real time', 'Take the effort out of determining tax rates and sell confidently across borders with automated tax management from Avalara AvaTax— including built-in VAT calculation when you sell into or across the EU and UK. Save time and stay compliant when you let Avalara do the heavy lifting.', '{}', 'pending', 'woocommerce.com', '2024-04-22 13:15:23', NULL, 0, 'plain', '', 0, 0, 'info'),
(55, 'woo-activation-survey-blockers-22AUG23', 'info', 'en_US', 'How can we help you get that first sale?', 'Your feedback is vital. Please take a minute to share your experience of setting up your new store and whether anything is preventing you from making those first few sales. Together, we can make Woo even better!', '{}', 'unactioned', 'woocommerce.com', '2024-04-22 13:17:15', NULL, 0, 'plain', '', 0, 0, 'info'),
(56, 'woocommerce-usermeta-Sept2023-productvendors', 'update', 'en_US', 'Your store requires a security update', '<strong>Your shop needs an update to address a vulnerability in WooCommerce.</strong> The fix was released on Sept 15. Please update WooCommerce to the latest version immediately. <a href=\"https://developer.woocommerce.com/2023/09/16/woocommerce-vulnerability-reintroduced-from-7-0-1/\" />Read our developer update</a> for more information.', '{}', 'pending', 'woocommerce.com', '2024-04-22 13:15:23', NULL, 0, 'plain', '', 0, 0, 'info'),
(57, 'woocommerce-STRIPE-Oct-2023-update-needed', 'update', 'en_US', 'Action required: Security update for WooCommerce Stripe Gateway', '<strong>Your shop requires a security update to address a vulnerability in the WooCommerce Stripe Gateway</strong>. The fix for this vulnerability was released on October 17. Please update immediately.', '{}', 'pending', 'woocommerce.com', '2024-04-22 13:15:23', NULL, 0, 'plain', '', 0, 0, 'info'),
(58, 'amazon-mcf-reviews-2023-12-07', 'marketing', 'en_US', 'Enjoying Amazon MCF for WooCommerce?', 'We\'re Never Settle, the developers behind Amazon MCF for WooCommerce, and would be deeply honored to have your review. Reviews help immensely as other users can learn how MCF can solve their needs too! Not happy or need help? Please reach out for support and we’d love to make things right!', '{}', 'pending', 'woocommerce.com', '2024-04-22 13:15:23', NULL, 0, 'plain', '', 0, 0, 'info'),
(59, 'remove_estimated_deposits_2024', 'marketing', 'en_US', 'Estimated deposits are going away', 'To provide more accurate deposit information and support the expansion of instant deposits, estimated deposit details will no longer be available in WooPayments. We recommend upgrading to the latest version of WooPayments for more detailed balance status information.', '{}', 'pending', 'woocommerce.com', '2024-04-22 13:15:23', NULL, 0, 'plain', '', 0, 0, 'info'),
(60, 'airwallex_q1_2024', 'marketing', 'en_US', 'Save big on foreign exchange (FX) fees', 'Boost your international sales by offering Apple Pay, Google Pay, and 60+ local payment methods with Airwallex. Save time and money by settling payments directly in USD, EUR, HKD, SGD, AUD, and more — without the extra FX fees. Simple, cost-effective, fast.', '{}', 'pending', 'woocommerce.com', '2024-04-22 13:15:23', NULL, 0, 'plain', '', 0, 0, 'info'),
(61, 'checkout_survey_new_users', 'survey', 'en_US', 'We\'d like your feedback on WooCommerce checkout', 'Your feedback is essential for us to improve the WooCommerce checkout and increase your conversion rate, turning more visitors into customers. Answer a few questions to help us understand your experience. It shouldn\'t take more than 5 minutes of your time.', '{}', 'pending', 'woocommerce.com', '2024-04-22 13:15:23', NULL, 0, 'plain', '', 0, 0, 'info'),
(62, 'wc-admin-customizing-product-catalog', 'info', 'en_US', 'Come personalizzare il tuo catalogo prodotti', 'Desideri che il tuo catalogo dei prodotti e le immagini abbiano un bell\'aspetto e siano in linea con il tuo marchio. Questa guida ti fornirà tutti i suggerimenti di cui hai bisogno per rendere i tuoi prodotti fantastici nel tuo negozio.', '{}', 'unactioned', 'woocommerce-admin', '2024-04-22 13:17:15', NULL, 0, 'plain', '', 0, 0, 'info'),
(63, 'wc-admin-wc-helper-connection', 'info', 'en_US', 'Connettiti a WooCommerce.com', 'Connettiti per ottenere notifiche e aggiornamenti sui prodotti importanti.', '{}', 'unactioned', 'woocommerce-admin', '2024-04-22 13:22:59', NULL, 0, 'plain', '', 0, 0, 'info');
